INSERT INTO `ap_about` (`id`, `name`, `content`, `addtime`, `ord`, `nid`) VALUES
(1, '关于我们', '&lt;h2 style=&quot;text-align:center;&quot;&gt;&lt;span style=&quot;color:#017f40;font-size:28px;&quot;&gt;苹果装饰&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ap/Public/admin/abc/php/upload/image/20180513/1526194005108784.jpg&quot; title=&quot;1526194005108784.jpg&quot; alt=&quot;20170612100825909.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ap/Public/admin/abc/php/upload/image/20180513/1526194056256039.png&quot; title=&quot;1526194056256039.png&quot; alt=&quot;201706121008591772.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;苹果装饰设计工程有限公司是一家集空间设计、装修、软装、集成为一体的装饰集团，2003年至今根据市场的不同需求，根据不同的品牌定位，苹果装饰集团完成了在家居全产业链的全方位布局。&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ap/Public/admin/abc/php/upload/image/20180513/1526194118117786.jpg&quot; title=&quot;1526194118117786.jpg&quot; alt=&quot;201706121010039289.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;家是具备成长性的，苹果装饰集团根据人在空间里使用的不同功能需求，完成了涵盖全服务链的品牌构建，2003-2013，苹果装饰的1.10时代专注工程质量，为数万户家庭提供高品质的装修服务。十年随之而来的优良口碑和声誉已然成为苹果装饰的核心竞争&lt;/p&gt;', 1526140800, 1, 5),
(3, '品牌历程', '&lt;p style=&quot;text-align: center; line-height: normal;&quot;&gt;&lt;span style=&quot;color:#017f40;font-size:28px;font-weight:normal;&quot;&gt;&lt;br/&gt;苹果装饰集团&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;color:#017f40;font-size:28px;font-weight:normal;&quot;&gt;成长大事件&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#017f40;font-size:28px;font-weight:normal;&quot;&gt;&lt;img src=&quot;/ap/Public/admin/abc/php/upload/image/20180513/1526195124139913.jpg&quot; title=&quot;1526195124139913.jpg&quot; alt=&quot;201706121043289181.jpg&quot; width=&quot;860&quot; height=&quot;161&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#017f40;font-size:28px;font-weight:normal;&quot;&gt;&lt;img src=&quot;/ap/Public/admin/abc/php/upload/image/20180513/1526195196130171.jpg&quot; title=&quot;1526195196130171.jpg&quot; alt=&quot;201706121041287163.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#017f40;font-size:28px;font-weight:normal;&quot;&gt;&lt;img src=&quot;/ap/Public/admin/abc/php/upload/image/20180513/1526195222605586.jpg&quot; title=&quot;1526195222605586.jpg&quot; alt=&quot;201706121041392503.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#017f40;font-size:28px;font-weight:normal;&quot;&gt;&lt;img src=&quot;/ap/Public/admin/abc/php/upload/image/20180513/1526195261900224.jpg&quot; title=&quot;1526195261900224.jpg&quot; alt=&quot;201706121041478326 (1).jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#017f40;font-size:28px;font-weight:normal;&quot;&gt;&lt;img src=&quot;/ap/Public/admin/abc/php/upload/image/20180513/1526195286896610.jpg&quot; title=&quot;1526195286896610.jpg&quot; alt=&quot;201706121041577735.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#017f40;font-size:28px;font-weight:normal;&quot;&gt;&lt;img src=&quot;/ap/Public/admin/abc/php/upload/image/20180513/1526195298358110.jpg&quot; title=&quot;1526195298358110.jpg&quot; alt=&quot;201706121042043841.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#017f40;font-size:28px;font-weight:normal;&quot;&gt;&lt;img src=&quot;/ap/Public/admin/abc/php/upload/image/20180513/1526195309350450.png&quot; title=&quot;1526195309350450.png&quot; alt=&quot;201706121011506149.png&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#017f40;font-size:28px;font-weight:normal;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#017f40;font-size:28px;font-weight:normal;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;', 1526140800, 2, 5),
(4, '加入我们', '&lt;div class=&quot;detail&quot;&gt;&lt;div class=&quot;join_container&quot;&gt;&lt;h3&gt;\r\n			我们希望你&lt;/h3&gt;&lt;div class=&quot;ability&quot;&gt;&lt;div class=&quot;value&quot;&gt;&lt;h4&gt;\r\n					做人符合苹果&amp;quot;价值观&amp;quot;&lt;/h4&gt;&lt;div class=&quot;list&quot;&gt;&lt;div&gt;&lt;h5&gt;\r\n							梦想&lt;/h5&gt;&lt;p&gt;\r\n							敢想敢做梦&lt;/p&gt;&lt;p&gt;\r\n							敢于挑战、超越&lt;/p&gt;&lt;p&gt;\r\n							实现自我&lt;/p&gt;&lt;/div&gt;&lt;div&gt;&lt;h5&gt;\r\n							踏实&lt;/h5&gt;&lt;p&gt;\r\n							专业专注&lt;/p&gt;&lt;p&gt;\r\n							重复10000小时&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;/div&gt;&lt;div&gt;&lt;h5&gt;\r\n							勤奋&lt;/h5&gt;&lt;p&gt;\r\n							身体在行动&lt;/p&gt;&lt;p&gt;\r\n							提高脑转速脑容量&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;/div&gt;&lt;div&gt;&lt;h5&gt;\r\n							责任&lt;/h5&gt;&lt;p&gt;\r\n							独立/自省&lt;/p&gt;&lt;p&gt;\r\n							主动发现问题&lt;/p&gt;&lt;p&gt;\r\n							解决问题&lt;/p&gt;&lt;/div&gt;&lt;div&gt;&lt;h5&gt;\r\n							创新&lt;/h5&gt;&lt;p&gt;\r\n							全局思维&lt;/p&gt;&lt;p&gt;\r\n							长远规划&lt;/p&gt;&lt;p&gt;\r\n							敢于质疑勇敢改变&lt;/p&gt;&lt;/div&gt;&lt;div&gt;&lt;h5&gt;\r\n							共享&lt;/h5&gt;&lt;p&gt;\r\n							借助公司平台&lt;/p&gt;&lt;p&gt;\r\n							实现梦想&lt;/p&gt;&lt;p&gt;\r\n							分享平台给员工&lt;/p&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;method&quot;&gt;&lt;h4&gt;\r\n					做事符合苹果&amp;quot;方法论&amp;quot;&lt;/h4&gt;&lt;div class=&quot;list&quot;&gt;&lt;div&gt;&lt;b&gt;目标&lt;/b&gt;&lt;span&gt;目标源于梦想/富有挑战性&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;b&gt;策略&lt;/b&gt;&lt;span&gt;制定目标完成策略/专业/创新&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;b&gt;执行&lt;/b&gt;&lt;span&gt;有标准/有节点/高效率&lt;/span&gt;&lt;/div&gt;&lt;div&gt;&lt;b&gt;总结&lt;/b&gt;&lt;span&gt;跟进检查/总结因不总结果&lt;/span&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;join_container&quot;&gt;&lt;h3&gt;\r\n			我们会给你&lt;/h3&gt;&lt;div class=&quot;list treatment&quot;&gt;&lt;p&gt;\r\n				基层员工优于行业标准的薪酬待遇&lt;/p&gt;&lt;p&gt;\r\n				优秀苹果员工年度出国游福利&lt;/p&gt;&lt;p&gt;\r\n				分公司总经理股份制&lt;/p&gt;&lt;p&gt;\r\n				股东没有上限的发展空间&lt;/p&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;join_container&quot;&gt;&lt;h3&gt;\r\n			我们在寻找&lt;/h3&gt;&lt;div class=&quot;list search&quot;&gt;&lt;div class=&quot;job&quot;&gt;&lt;div&gt;\r\n					服务社经理&lt;/div&gt;&lt;span&gt;职位介绍：在苹果装饰，没有某职能部门经理，我们店面的主人，是多个服务社经理 用你在行业多年的经验，服务好员工，服务好客户。&lt;/span&gt;&lt;/div&gt;&lt;div class=&quot;job&quot;&gt;&lt;div&gt;\r\n					设计师&lt;/div&gt;&lt;span&gt;职位介绍：家装设计师对客户的家负责，必须2年以上经验才能接单，负责客户房屋 从毛坯到入住全过程的设计服务。&lt;/span&gt;&lt;/div&gt;&lt;div class=&quot;job&quot;&gt;&lt;div&gt;\r\n					设计师助理&lt;/div&gt;&lt;span&gt;职位介绍：学徒职位，师父领进门，修行靠自己。见事做事，提升专业，对结果负 责。朝着设计师职位看齐。&lt;/span&gt;&lt;/div&gt;&lt;div class=&quot;job&quot;&gt;&lt;div&gt;\r\n					家装顾问&lt;/div&gt;&lt;span&gt;职位介绍：发挥你优秀的社交能力和人品，尽一切可能的挖掘需要装修的客户。跟 进、签单、服务好客户。&lt;/span&gt;&lt;/div&gt;&lt;div class=&quot;job&quot;&gt;&lt;div&gt;\r\n					前台客服&lt;/div&gt;&lt;span&gt;职位介绍：作为客户进公司第一眼看到的门面，你需要举止端庄、体面。快速反应， 细心服务客户。&lt;/span&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;', 1526140800, 3, 5);

